import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';

// Correction du bug d’icône de Leaflet
import L from 'leaflet';
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

function App() {
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/api/hello')
      .then(res => res.json())
      .then(data => setMessage(data.message));
  }, []);
  return (
    <div style={{ height: '100vh', width: '80%' }}>
      <MapContainer
        center={[48.8566, 2.3522]}
        zoom={12}
        scrollWheelZoom={false}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[48.8413889, 2.2530555555555556]}>
          <Popup>
            Stade du Parc des Princes<br />Football - Domicile du PSG Football <br /> PSG - Reims
          </Popup>
        </Marker>
        <Marker position={[48.8433333, 2.2530555555555556]}>
          <Popup>
            Stade Jean-Bouin <br />Football - Domicile du Paris Football Club<br /> Paris FC - Olympique de Marseille
          </Popup>
        </Marker>
        <Marker position={[48.8186111, 2.346388888888889]}>
          <Popup>
            Stade Sébastien Charléty <br />Baseball - Domicile du Paris Université Club<br />Football - Domicile du Paris Football Club Féminin<br /> Paris FC - Olympique de Marseille
          </Popup>
        </Marker>
        <Marker position={[48.8994444, 2.361388888888889]}>
          <Popup>
            <h2>Arena Porte de la Chapelle</h2> <br /><h3>Basketball - Domicile du Paris Basketball</h3><br /> Paris Basket - Le Mans
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
}

export default App;
