import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useCallback, useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import './App.css'
import DatePicker from "react-datepicker"

import "react-datepicker/dist/react-datepicker.css"

// Correction du bug d’icône de Leaflet
import L from 'leaflet';
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

async function searchMatchBetweenDates(startDate, endDate) {
  var result = null;
  const response = await fetch('http://localhost:3001/api/matchs?startDate=' + encodeURIComponent(startDate) + '&endDate=' + encodeURIComponent(endDate));
  return response.json();
}

class App extends Component {
  constructor(props){
    super(props);
    this.performSearch = this.performSearch.bind(this);
    this.matchListRef = React.createRef();

    this.state = {
      startdate: new Date(),
      enddate: new Date()
    }
  }

  addElementsToMatchList(elements){
    elements.forEach((element) => {
      var content = document.createElement("li");
      content.innerHTML = "<div> <p> stade: " + element.stade + "</p> \
                          <p> compétition: "+element.competition+"</p>\
                          <p> "+element.equipes[0]+" VS "+element.equipes[1]+"</p></div>";
      this.matchListRef.current.appendChild(content);

    });
  }

  async performSearch() {
    const result = await searchMatchBetweenDates(this.state.startdate, this.state.enddate);
    this.addElementsToMatchList(result);
  }

  render() {
    return (
      <div className="container" >
        <div className="map" >
          <MapContainer
            center={[48.8566, 2.3522]}
            zoom={12.5}
            scrollWheelZoom={false}
            style={{width: "100%", height: "100%"}}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {/*<Marker position={[48.8413889, 2.2530555555555556]}>
              <Popup>
                Stade du Parc des Princes<br />Football - Domicile du PSG Football <br /> PSG - Reims
              </Popup>
            </Marker>
            <Marker position={[48.8433333, 2.2530555555555556]}>
              <Popup>
                Stade Jean-Bouin <br />Football - Domicile du Paris Football Club<br /> Paris FC - Olympique de Marseille
              </Popup>
            </Marker>
            <Marker position={[48.8186111, 2.346388888888889]}>
              <Popup>
                Stade Sébastien Charléty <br />Baseball - Domicile du Paris Université Club<br />Football - Domicile du Paris Football Club Féminin<br /> Paris FC - Olympique de Marseille
              </Popup>
            </Marker>
            <Marker position={[48.8994444, 2.361388888888889]}>
              <Popup>
                <h2>Arena Porte de la Chapelle</h2> <br /><h3>Basketball - Domicile du Paris Basketball</h3><br /> Paris Basket - Le Mans
              </Popup>
            </Marker>*/}
          </MapContainer>
        </div>
        <div className="list">
          <h2>Période</h2>
          Entre <DatePicker dateFormat="dd/MM/yyyy" selected={this.state.startdate} onChange={(date) => this.setState({startdate: date})} />
          <br/>
          Et <DatePicker dateFormat="dd/MM/yyyy" selected={this.state.enddate} onChange={(date) => this.setState({enddate: date})} />
            <br/>
          <button onClick={this.performSearch}>Rechercher</button>
          <br/>
          <h3>Matchs</h3>
          <ul ref={this.matchListRef}>
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
